import React, {useState,useEffect,useRef} from "react";
import axios from 'axios';
import {Link,useNavigate} from 'react-router-dom';
import '../LandingPage.css';
import SearchBar from "../components/SearchBar";
import { jwtDecode } from "jwt-decode";
import Navbar from "../components/Navbar";


export default function LandingPage(){
    const [mangas,setMangas] = useState([]);
    const [currentPage,setCurrentPage] = useState(1);
    const [totalPages,setTotalPages] = useState(1);
    const [loading,setLoading] = useState(true);
    const [hoveredManga,setHoveredManga] = useState(null);
    const [mousePosition,setMousePosition] = useState({ x: 0, y: 0});
    const [searchTerm,setSearchTerm] = useState("");
    const [isModalOpen,setIsModalOpen] = useState(false);
    const [user,setUser] = useState(null);
    const mangasPerPage = 24;
    const mangaCardRef = useRef(null);
    const hoverWindowRef = useRef(null);
    const navigate = useNavigate();
    const [recentMangas,setRecentMangas] = useState([]);
    const [recentMangasIndex,setRecentMangasIndex] = useState(0);

    useEffect(() => {
        fetchMangas();
        fetchRecentMangas();
        const token = localStorage.getItem("token");
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                setUser(decodedToken);
            } catch (e) {
                console.error("Invalid token");
            }
        }
    }, [currentPage,searchTerm]);

    const fetchRecentMangas = async () => {
        try {
            const response = await axios.get(`http://127.0.0.1:5000/recently_added`);
            setRecentMangas(response.data.mangas);
        }
        catch (e) {
            console.error("error fetching recently added mangas",e);
        }
    }

    const handleLogout = () => {
        localStorage.removeItem("token");
        setUser(null);
        navigate("/login");
        setIsModalOpen(false);
    };

    const handleLogin = () => {
        setIsModalOpen(false);
        navigate("/login");
    }

    const handleRegister = () => {
        setIsModalOpen(false);
        navigate("/register");
    }



    const fetchMangas = async () => {
        setLoading(true);
        try {
            const response = await axios.get(`http://127.0.0.1:5000/mangas?page=${currentPage}&per_page=${mangasPerPage}&search=${searchTerm}`);
            setMangas(response.data.mangas);
            setTotalPages(response.data.total_pages);


        } catch (error) {
            console.error("Error fetching mangas",error);
        } finally {
            setLoading(false);
        }
    };

    const handlePageChange = (pageNumber) => {
        if (pageNumber >= 1 && pageNumber <= totalPages) {
            setCurrentPage(pageNumber)
        }
    };

    const renderPagination = () => {
        const pageButtons = [];
        for (let i = 1;i<= totalPages;i++) {
            pageButtons.push(
                <li key={i} className={`page-item ${i === currentPage ? `active` : ``}`}>
                    <button onClick={() => handlePageChange(i)} className="page-link">{i}</button>
                </li>
            );
        }
        return (
            <nav aria-label="Page navigation">
                <ul className="pagination justify-content-center">
                    <li className={`page-item ${currentPage === 1 ? `disabled` : ``}`}>
                        <button onClick={() => handlePageChange(currentPage - 1)} className="page-link" tabIndex="-1" aria-disabled="true">Previous</button>
                    </li>
                    {pageButtons}
                    <li className={`page-item ${currentPage === totalPages ? `disabled` : ``}`}>
                        <button onClick={() => handlePageChange(currentPage + 1)} className="page-link" tabIndex="-1" aria-disabled="true">Next</button>
                    </li>
                </ul>
            </nav>
        );

    };


    const handleMouseEnter = (event,manga) => {
        const windowHeight = window.innerHeight;
        const windowWidth = window.innerWidth;
        const hoverWindowWidth = 400;
        const hoverWindowHeight = 300;
        let hoverY = event.clientY + 10;
        let hoverX = event.clientX + 10;

        if (event.clientY + 10 + hoverWindowHeight > windowHeight) {
            hoverY = event.clientY - hoverWindowHeight - 10;
        }

        if (event.clientX + 10 + hoverWindowWidth > windowWidth) {
            hoverX = event.clientX - hoverWindowWidth - 10;
        }
        setMousePosition({
            x: hoverX, y: hoverY
        });
        setHoveredManga(manga);
    };

    const handleMouseLeave = () => {
        setHoveredManga(null);
    };

    const handleSearch = (term) => {
        setSearchTerm(term);
        setCurrentPage(1,() => {

        })
    };

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    }

    const handlePrevManga = () => {
        setRecentMangasIndex(prevIndex => (prevIndex === 0 ? recentMangas.length - 1 : prevIndex - 1));
    }

    const handleNextManga = () => {
        setRecentMangasIndex(prevIndex => (prevIndex === recentMangas.lenght - 1 ? 0 : prevIndex + 1));
    }


    return (
        <div className="mt-4">
             

            {recentMangas.length > 0 && (
                <div className="recent-manga-container" style={{ backgroundImage: `url(${recentMangas[recentMangasIndex]?.imageUrl})`}}>
                    <div className="recent-manga-content">
                        <h2 className="recent-manga-header">
                            Recently added Manga
                        </h2>
                        <div className="recent-manga-details d-flex align-items-start">
                            <img className="recent-manga-image" src={recentMangas[recentMangasIndex]?.imageUrl} alt={recentMangas[recentMangasIndex]?.title} />
                        <div className="recent-manga-text">
                            <h1 className="recent-manga-title">{recentMangas[recentMangasIndex]?.title}</h1>
                            <div className="recent-manga-tags">
                                {recentMangas[recentMangasIndex]?.tags?.map((tag,index) => (
                                    <span key={index} className="recent-manga-tag">
                                        {tag}
                                    </span>
                               ))}
                            </div>
                            <p className="recent-manga-description">{recentMangas[recentMangasIndex]?.description ?
                            recentMangas[recentMangasIndex]?.description.slice(0,1000) + "...": "No Description"}</p>
                        </div>
                        </div>
                           <div className="recent-manga-nav d-flex justify-content-end align-items-center">
                            <p>No.{recentMangasIndex + 1}</p>
                            <button onClick={handlePrevManga}>{"<"}</button>
                            <button onClick={handleNextManga}>{">"}</button>
                            </div>     
                    </div>
                </div>
            )}




            <div className="container mt-4">
                {loading ? (
                    <p>Loading mangas...</p>
                ) : (
                    <div className="row">
                        {mangas.map(manga => (
                            <div key={manga.id} className="col-md-4 mb-4"
                                onMouseEnter={(event) => handleMouseEnter(event,manga)}
                                onMouseLeave={handleMouseLeave}
                                ref = {mangaCardRef}
                                >
                                <div className="card">
                                    <img src={manga.imageUrl} alt={manga.title} className="card-img-top" style={{ height: '300px', objectFit: 'cover'}}/>
                                    <div className="card-body">
                                        <h5 className="card-title">{manga.title}</h5>
                                        <p className="card-text">Publication Year: {manga.publicationYear}</p>
                                    </div>
                                </div>
                                {hoveredManga && hoveredManga.id === manga.id && (
                                    <div 
                                    className="manga-hover-window"
                                    style={{left: mousePosition.x + 10,
                                            top: mousePosition.y + 10,
                                    }}
                                    >
                                        <h5 className="hover-title">{manga.title}</h5>
                                        <div className="hover-content">
                                        <img src={manga.imageUrl} alt={manga.title} className="hover-img" />
                                        <div className="hover-details">
                                            <p><b>Author:</b> {manga.author}</p>
                                            <p><b>Publication Year:</b> {manga.publicationYear}</p>
                                            <p><b>Rating:</b> {manga.ratings.length > 0 ? manga.ratings.reduce((sum,rating) => sum + rating.rating,0) / manga.ratings.length : 'No Ratings'}</p>
                                        
                                            <p><b>Tags:</b> {manga.tags.join(", ")}</p>
                                        </div>
                                        </div>
                                    <p className="hover-description"><b>Summary:</b> {manga.description ? manga.description.slice(0,150) + "..." : "No Description"}</p>
                                    </div>
                                )}



                                </div>
                        ))}
                        </div>
                )}
                {renderPagination()}
                </div>
        </div>
    );
}
