import React, { useState,useEffect } from 'react';
import './App.css';



import {BrowserRouter,Routes,Route,useNavigate} from 'react-router-dom';

import LandingPage from "./pages/LandingPage"
import LoginPage from "./pages/LoginPage"
import RegisterPage from "./pages/RegisterPage"
import Navbar from './components/Navbar';
import SearchResults from './pages/SearchResults';
import {jwtDecode} from 'jwt-decode';

function App() {
  const [isMenuOpen,setIsMenuOpen] = useState(false);
  const [user,setUser] = useState(null);
  const [isModalOpen,setIsModalOpen] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
            if (token) {
                try {
                    const decodedToken = jwtDecode(token);
                    setUser(decodedToken);
                } catch (e) {
                    console.error("Invalid token");
                }
            }
  },[]);

  const handleMenuToggle = (isOpen) => {
       setIsMenuOpen(isOpen);
    }



  const handleLogout = () => {
      localStorage.removeItem("token");
      setUser(null);
      navigate("/login");
      setIsModalOpen(false);
  };

  const handleLogin = () => {
      setIsModalOpen(false);
      navigate("/login");
  }

  const handleRegister = () => {
      setIsModalOpen(false);
      navigate("/register");
  }

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  }

  return (
    <div className="vh-100 gradient-custom">
      <div className="container">


      <BrowserRouter>
      <div style={{ position: 'relative',  left: isMenuOpen ? '250px' : '0', transition: 'left 0.3s ease'}}>
      <Routes>
      <Route path="/*" element = {<Navbar onMenuToggle = {handleMenuToggle} user = {user} handleLogout = {handleLogout} handleLogin = {handleLogin} handleRegister={handleRegister} setIsModalOpen = {toggleModal} />} />
        <Route path="/" element={<LandingPage />}/>
        <Route path="/search" element={<SearchResults />} />
        <Route path="/login" element={<LoginPage />}/>
        <Route path="/register" element={<RegisterPage />}/>


      </Routes>
      </div>
      </BrowserRouter>
      </div>

    </div>
  );
}

export default App;
