import React, {useState, useEffect, useRef} from "react";
import axios from 'axios';
import {Link, useNavigate} from 'react-router-dom';
import '../LandingPage.css';
import SearchBar from "../components/SearchBar";
import { jwtDecode } from "jwt-decode";

export default function LandingPage(){
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isModalOpen,setIsModalOpen] = useState(false);
     const [user, setUser] = useState(null);
     const navigate = useNavigate();
    const [recentMangas,setRecentMangas] = useState([]);
    const [recentMangasIndex,setRecentMangasIndex] = useState(0);


    useEffect(() => {
        fetchRecentMangas();
         const token = localStorage.getItem('token');
         if (token) {
            try {
                const decodedToken = jwtDecode(token);
                 setUser(decodedToken);

              } catch (e) {
               console.error("invalid token");
             }
            }
    }, []);

    const fetchRecentMangas = async () => {
        try {
            const response = await axios.get(`http://127.0.0.1:5000/recently_added`);
            setRecentMangas(response.data.mangas);
        }
        catch (e) {
            console.error("error fetching recently added mangas",e);
        }
    }

    const handleLogout = () => {
          localStorage.removeItem('token');
          setUser(null);
         navigate("/login");
        setIsModalOpen(false);

      };
     const handleLogin = () =>{
        setIsModalOpen(false)
        navigate("/login")
     }
    const handleRegister = () => {
       setIsModalOpen(false);
      navigate("/register")
    }


    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    }

    const handlePrevManga = () => {
        setRecentMangasIndex(prevIndex => (prevIndex === 0 ? recentMangas.length - 1 : prevIndex - 1));
    }

    const handleNextManga = () => {
        setRecentMangasIndex(prevIndex => (prevIndex === recentMangas.length - 1 ? 0 : prevIndex + 1));
    }

    const handleSearch = (term) => {
      navigate(`/mangas?search=${term}`)
     };

    return (
        <div className="mt-4">
            <nav className="navbar navbar-light bg-light">
               <div className="container d-flex justify-content-between align-items-center">
                   <div className = "d-flex align-items-center" >
                    <button className="navbar-toggler" type="button"  onClick={toggleMenu} >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                  <Link to="/" className="navbar-brand">Manga App</Link>
                  </div>
                 <div className="d-flex align-items-center">
                        <SearchBar onSearch={handleSearch} />

                           <button className="user-button" onClick={toggleModal}>
                             <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" className="bi bi-person-circle" viewBox="0 0 16 16">
                                   <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0z"/>
                                   <path fillRule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8zm8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1z"/>
                             </svg>
                            </button>

                           {isModalOpen &&(
                            <div className="user-modal">
                                <div className="user-modal-header">
                                    {user ? <p>{user.username}</p> : <p>Guest</p>}
                                </div>
                            <div className = "user-modal-body">
                                 {!user && (
                                    <div className = "d-flex flex-column">
                                      <button onClick={handleLogin} className = "btn btn-primary btn-sm login-button" >Login</button>
                                        <button onClick = {handleRegister} className="btn btn-secondary btn-sm register-button">Register</button>
                                   </div>
                                 )}
                                {user && (
                                   <button onClick = {handleLogout} className="btn btn-danger btn-sm">Logout</button>
                                 )}
                                </div>
                           </div>
                            )}
                  </div>
                </div>
            </nav>

           {isMenuOpen && (
            <div className="side-menu">
                 <div className="side-menu-header">
                      <h5 className = "side-menu-title">Manga App</h5>
                       <button className="close-menu-button" onClick={toggleMenu}>X</button>
                    </div>
                <ul className="side-menu-list">
                     <li className="side-menu-item">
                        <strong >Follows</strong>
                            <ul className="sub-menu">
                                <li>
                                 <Link to="/" onClick={toggleMenu} >Updates</Link>
                                </li>
                                <li>
                                    <Link to="/" onClick={toggleMenu}>Library</Link>
                                </li>
                                <li>
                                     <Link to="/" onClick={toggleMenu}>Reading history</Link>
                                </li>
                            </ul>
                    </li>
                     <li className="side-menu-item">
                       <strong >Titles</strong>
                          <ul className="sub-menu">
                            <li>
                                <Link to="/" onClick={toggleMenu}>Advanced search</Link>
                             </li>
                            <li>
                                <Link to="/" onClick={toggleMenu}>Recently added</Link>
                             </li>
                            <li>
                                 <Link to="/" onClick={toggleMenu}>Random</Link>
                            </li>
                         </ul>
                   </li>
                  </ul>
               </div>
              )}
               {recentMangas.length > 0 && (
                <div className="recent-manga-container" style={{ backgroundImage: `url(${recentMangas[recentMangasIndex]?.imageUrl})`}}>
                    <div className="recent-manga-content">
                        <h2 className="recent-manga-header">
                            Recently added Manga
                        </h2>
                        <div className="recent-manga-details d-flex align-items-start">
                            <img className="recent-manga-image" src={recentMangas[recentMangasIndex]?.imageUrl} alt={recentMangas[recentMangasIndex]?.title} />
                        <div className="recent-manga-text">
                            <h1 className="recent-manga-title">{recentMangas[recentMangasIndex]?.title}</h1>
                            <div className="recent-manga-tags">
                                {recentMangas[recentMangasIndex]?.tags?.map((tag,index) => (
                                    <span key={index} className="recent-manga-tag">
                                        {tag}
                                    </span>
                               ))}
                            </div>
                            <p className="recent-manga-description">{recentMangas[recentMangasIndex]?.description ?
                            recentMangas[recentMangasIndex]?.description.slice(0,300) + "...": "No Description"}</p>
                        </div>
                        </div>
                           <div className="recent-manga-nav d-flex justify-content-end align-items-center">
                            <p>No. {recentMangasIndex + 1}</p>
                            <button onClick={handlePrevManga}>{"<"}</button>
                            <button onClick={handleNextManga}>{">"}</button>
                            </div>     
                    </div>
                </div>
            )}

    </div>
    );
}
