import React from 'react';
import { useSearchParams,useLocation} from 'react-router-dom';
import MangaList from '../components/MangaList';

const SearchResults = () => {
    const [searchTerm,setSearchTerm] = useState("");
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const searchQuery = queryParams.get("search")

    useEffect(() => {
        if(searchQuery) {
            setSearchTerm(searchQuery);
        }
    },[searchQuery]);

    const recordSearch = async(mangaId) => {
        try {
            await axios.post(`http://127.0.0.1:5000/search-history`, {
                manga_id : mangaId
            },
            {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            })
        } catch (e) {
            console.error("Error recoring search history", error);
        }
    }

    return (
        <div style={{ position: 'relative'}}> 
            <div className="container mt-4">
            <MangaList url="http://127.0.0.1:5000/mangas?" mangasPerPage={24} sortBy="created_at" order="desc" searchTerm={searchTerm} recordSearch title={`Search results for "${searchTerm}"`} />
            </div>
        </div>
    )
}

export default SearchResults;
