import React, { useState,useEffect } from 'react';
import './App.css';



import {BrowserRouter,Routes,Route} from 'react-router-dom';

import LandingPage from "./pages/LandingPage"
import LoginPage from "./pages/LoginPage"
import RegisterPage from "./pages/RegisterPage"
import Navbar from './components/Navbar';
import SearchResults from './pages/SearchResults';
import AdvancedSearch from './pages/AdvancedSearch';
import {jwtDecode} from 'jwt-decode';
import ProtectedRoute from './components/ProtectedRoute'
import MangaDetailsPage from './pages/MangaDetailsPage';

function App() {
  const [isMenuOpen,setIsMenuOpen] = useState(false);
  const [user,setUser] = useState(null);
  const [isModalOpen,setIsModalOpen] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
            if (token) {
                try {
                    const decodedToken = jwtDecode(token);
                    setUser(decodedToken);
                } catch (e) {
                    console.error("Invalid token");
                    localStorage.removeItem("token");
                    setUser(null);
                }
            }
  },[]);

  const handleMenuToggle = (isOpen) => {
       setIsMenuOpen(isOpen);
    }


  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  }

  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
  }

  return (
    <div className="vh-100 gradient-custom">
      <div className="container">


      <BrowserRouter>
      <div style={{ position: 'relative',  left: isMenuOpen ? '100px' : '0', transition: 'left 0.3s ease'}}>
      <Navbar onMenuToggle = {handleMenuToggle} user = {user}  setIsModalOpen = {toggleModal} logout={logout} />
      <Routes>
        <Route path="/" element={<LandingPage />}/>
        <Route path="/search" element={<ProtectedRoute><SearchResults/></ProtectedRoute>} />
        <Route path="/login" element={<LoginPage setUser={setUser}/>}/>

        <Route path="/register" element={<RegisterPage />}/>
        <Route path="/advanced-search" element={<ProtectedRoute><AdvancedSearch /></ProtectedRoute>} />
        <Route path="/mangas/:mangaId" element={<MangaDetailsPage />}/>

      </Routes>
      </div>
      </BrowserRouter>
      </div>

    </div>
  );
}

export default App;
