import React, {useState,useEffect} from "react";
import axios from 'axios';
import '../LandingPage.css';
import SmallMangaList from "../components/smallMangaList";
import {Link} from "react-router-dom";


export default function LandingPage(){
    const mangasPerPage = 24;
    const [featuredMangas,setFeaturedMangas] = useState([]);
    const [featuredMangasIndex,setFeaturedtMangasIndex] = useState(0);
    
    
    

    useEffect(() => {
        fetchFeaturedMangas();   
    }, []);

    const fetchFeaturedMangas = async () => {
        try {
            const response = await axios.get(`http://127.0.0.1:5000/featured`);
            setRecentMangas(response.data.mangas);
        }
        catch (e) {
            console.error("error fetching recently added mangas",e);
        }
    }
   
    const handlePrevManga = () => {
        setRecentMangasIndex(prevIndex => (prevIndex === 0 ? recentMangas.length - 1 : prevIndex - 1));
    }

    const handleNextManga = () => {
        setRecentMangasIndex(prevIndex => (prevIndex === recentMangas.length - 1 ? 0 : prevIndex + 1));
    }


    const recordSearch = async (mangaId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.log("User not logged in, search not recorded");
            return
        }
        try {
             await axios.post(`http://127.0.0.1:5000/search-history`,{
                 mangaId:mangaId
           },
           {
               headers: {
                 'Authorization': `Bearer ${token}`
                }
           })
         } catch (error) {
              console.error("Error recording search history:", error);
         }
    }

    return (
        <div>
            {recentMangas.length > 0 && (
                <div className="recent-manga-container" style={{ backgroundImage: `url(${recentMangas[recentMangasIndex]?.imageUrl})`,
                }}>
                    <div className="recent-manga-content">
                        <h2 className="recent-manga-header">
                            Recently added Manga
                        </h2>
                        <div className="recent-manga-details d-flex align-items-start">
                           
                            <img className="recent-manga-image" src={recentMangas[recentMangasIndex]?.imageUrl} alt={recentMangas[recentMangasIndex]?.title} />
                       
                        <div className="recent-manga-text">
                        <Link to={`/mangas/${recentMangas[recentMangasIndex]?.id}`} className="manga-square-title-link" onClick={() => recordSearch(recentMangas[recentMangasIndex]?.id)}>
                            <h1 className="recent-manga-title">{recentMangas[recentMangasIndex]?.title}</h1>
                            </Link>
                            <div className="recent-manga-tags">
                                {recentMangas[recentMangasIndex]?.tags?.map((tag,index) => (
                                    <Link
                                    key = {index}
                                    to = {`/advanced-search?tags=${tag}`}
                                    className="manga-tag-link"
                                    >
                                    <span key={index} className="recent-manga-tag">
                                        {tag}
                                    </span>
                                    </Link>
                               ))}
                            </div>
                            <p className="recent-manga-description">{recentMangas[recentMangasIndex]?.description ?
                            recentMangas[recentMangasIndex]?.description.slice(0,1000) + "...": "No Description"}</p>
                        </div>
                        </div>
                           <div className="recent-manga-nav d-flex justify-content-end align-items-center">
                            <p>No.{recentMangasIndex + 1}</p>
                            <button onClick={handlePrevManga}>{"<"}</button>
                            <button onClick={handleNextManga}>{">"}</button>
                            </div>     
                    </div>
                </div>
            )}

            <div className="container mt-4">
                <SmallMangaList url="http://127.0.0.1:5000/mangas?sort_by=created_at&order=desc" mangasPerPage={mangasPerPage} recordSearch={recordSearch}  title="Recently added"/>
                <SmallMangaList url="http://127.0.0.1:5000/mangas?sort_by=rating&order=desc" mangasPerPage={12} recordSearch={recordSearch}   title="Popular updates"/>
                <SmallMangaList url="http://127.0.0.1:5000/mangas?is_featured=true" mangasPerPage={12} recordSearch={recordSearch} title="Featured"/>
            
            </div>
           
                <br></br>
            
        </div>
    );
}
